import React from 'react';

const seasonConfig = {
    'summer': {
        'text': 'Lets go to beach!!',
        'iconName': 'sun'
    },
    'winter': {
        'text': 'Burr, it\'s chilly',
        'icon': 'snowflake'
    }
}

const getSeason = (lat) => {
    const currentMonth = new Date().getMonth();
    if(lat>0){
        if(currentMonth>2 && currentMonth <9)
            return 'summer';
        else
            return 'winter';
    }
    else if(lat<0){
        if(currentMonth>2 && currentMonth <9)
            return 'winter';
        else
            return 'summer';
    }
}

const SeasonDisplay = (props) =>{
    const season = getSeason(props.lat);
    return (
        <div>
            <i class={seasonConfig[season].iconName}></i>
            <div>{seasonConfig[season].text}</div>
            <i class={seasonConfig[season].iconName}></i>
        </div>
    );
}

export default SeasonDisplay;
