import React from 'react';

const seasonConfig = {
    'summer': {
        'text': 'Lets go to beach!!',
        'iconName': 'sun'
    },
    'winter': {
        'text': 'Burr, it\'s chilly',
        'icon': 'snowflake'
    }
}

const getSeason = (lat) => {
    const currentMonth = new Date().getMonth();
    if(lat>0){
        if(currentMonth>2 && currentMonth <9)
            return 'summer';
        else
            return 'winter';
    }
    else if(lat<0){
        if(currentMonth>2 && currentMonth <9)
            return 'winter';
        else
            return 'summer';
    }
}

const SeasonDisplay = (props) =>{
    return <div>
        Latitude - {props.lat}
        Season - {getSeason(props.lat)}
    </div>;
}

export default SeasonDisplay;
